      console.log('📝 准备保存用户资料...');
      console.log('👤 已知身份角色数:', window.userProfileData.knownIdentityCharacters?.length || 0);
      console.log('👤 已知身份角色列表:', window.userProfileData.knownIdentityCharacters || []);

